import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      credentials: {
        username: null,
        password: null
      },
      rules: {
        required: value => !!value || "Please, fill the field!",
        counterHigher: value => value.length >= 4 || "Minimal count of characters must be greater than 4 or equal!",
        counterLower: value => value.length < 20 || "Maximal count of characters must be lower than 20!"
      }
    };
  },
  methods: {
    register() {
      this.requestLoading = !this.requestLoading;
      axios.post("http://192.168.1.110:8000/users/register/", {
        username: this.credentials.username,
        password: this.credentials.password
      }).then(response => {
        if (response.status == 201) {
          this.requestLoading = !this.requestLoading;
        }
      });
    }
  },
  computed: {
    usernameError() {
      const errors = [];
      if (this.$vuetify.username.length < 5) {
        errors.push(9090);
        return errors;
      }
      return errors;
    }
  }
};
