import { useTheme } from "vuetify";
import { mapState } from "vuex";
export default {
  name: "SettingsView",
  data() {
    return {
      themeMode: useTheme(),
      darkMode: true
    };
  },
  methods: {
    changeTheme() {
      this.themeMode.global.name = this.darkMode ? "darkMode" : "lightMode";
      localStorage.setItem("theme", this.darkMode);
    }
  },
  computed: {
    ...mapState("theme")
  }
};
