import { useTheme } from "vuetify";
import NavDrawer from "@/components/NavDrawer.vue";
import { mapState, mapActions } from "vuex";
export default {
  name: "AppBar",
  components: {
    NavDrawer
  },
  data() {
    return {
      theme: useTheme(),
      darkMode: false
    };
  },
  methods: {
    changeTheme() {
      this.darkMode = !this.darkMode;
      this.theme.global.name = this.darkMode ? "darkTheme" : "lightTheme";
    },
    openDrawer() {
      this.$refs.navDrawer.openDrawer();
    }
  },
  computed: {
    ...mapState(["isAuthenticated", "user"]),
    ...mapActions(["setUser"])
  },
  created() {
    const userState = this.$cookies.get("user");
    if (userState) {
      this.setUser(userState, true);
    }
  }
};
