import { createStore } from "vuex";

export default createStore({
  state: {
    user: null,
    isAuthenticated: false,
  },
  getters: {},
  mutations: {
    setUser(state, user, authState) {
      state.user = user;
      state.isAuthenticated = authState;
    },
  },
  actions: {
    setUser(context, user, authState) {
      context.commit("setUser");
    },
  },
  modules: {},
});
