import axios from "axios";
import usernameRules from "@/secondary_scripts/rules.js";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      username: "",
      password: "",
      isValid: true,
      rules: usernameRules
    };
  },
  methods: {
    register() {
      if (this.isValid == true) {
        this.requestLoading = !this.requestLoading;
        axios.post("http://192.168.1.110:8000/users/register/", {
          username: this.username,
          password: this.password
        }).then(response => {
          if (response.status == 201) {
            this.requestLoading = !this.requestLoading;
          }
        });
      }
    }
  },
  watch: {}
};
