import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      alert: false,
      username: "",
      password: "",
      isValid: true,
      usernameRules: {
        required: value => !!value || "Please, fill the field!",
        minLength: value => value.length >= 4 || "Minimal length of username is 4",
        whiteSpace: value => !/\s/.test(value) || "The username must be without withespace!",
        maxLength: value => value.length <= 20 || "Maximal length of username is 20",
        nonWord: value => !/[^a-zA-Z0-9_]/.test(value) || "The username must be in Latin letters!"
      },
      passwordRules: {
        required: value => !!value || "Please, fill the field!",
        maxLength: value => value.length <= 15 || "Maximal length of password is 15",
        minLength: value => value.length >= 4 || "Minimal length of username is 4",
        numberRequired: value => /\d/.test(value) || "Password must contain at least one number",
        whiteSpace: value => !/\s/.test(value) || "The password must be without withespace!",
        upperCase: value => /[A-Z]/.test(value) || "The password must contain at least one Upper case!",
        lowerCase: value => /[a-z]/.test(value) || "The password must contain at least one lower case!",
        nonWord: value => !/[^a-zA-Z0-9_]/.test(value) || "The password must be in Latin letters!"
      },
      rulesDescription: "When you create an account use these standarts to make strong\
        username and secure password: number in password, at least one\
        upper case letter, at least one lower case letter, no whitespace,\
        min length of password must be greater than 4, max length of password\
        must be lower than 20, the letters of password must be in Latin."
    };
  },
  methods: {
    register() {
      if (this.isValid == true) {
        this.requestLoading = true;
        try {
          axios.post("http://192.168.1.110:8000/users/register/", {
            username: this.username,
            password: this.password
          }).then(response => {
            if (response.status == 201 || response.status == 200) {
              this.requestLoading = false;
              this.$cookies.set("user", this.username, "1d");
              this.$nextTick(() => {});
              this.$router.push("/");
            }
            if (response.status == 226) {
              this.requestLoading = false;
            }
            if (response.status == 400) {
              this.requestLoading = false;
            }
          });
          this.requestLoading = !this.requestLoading;
        } catch (err) {
          alert(err.message);
        }
      }
    }
  }
};
