import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      username: "",
      password: "",
      isValid: false,
      rules: {
        required: value => !!value || "Please, fill the field!",
        maxLength: value => value.length <= 20 || "Your username must contain max 20 characters!",
        minLength: value => value && value.length >= 4 || "Your username must contain at least 4 characters!"
      }
    };
  },
  methods: {
    register() {
      if (this.isValid == true) {
        this.requestLoading = !this.requestLoading;
        axios.post("http://192.168.1.110:8000/users/register/", {
          username: this.username,
          password: this.password
        }).then(response => {
          if (response.status == 201) {
            this.requestLoading = !this.requestLoading;
          }
        });
      }
    },
    onSubmit() {
      if (this.isValid) {
        alert(90);
      } else {
        alert(0);
      }
    }
  }
};
