import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      credentials: {
        username: null,
        password: null
      }
    };
  },
  methods: {
    register() {
      this.requestLoading = !this.requestLoading;
      axios.post("http://192.168.1.110:8000/users/register/", {
        username: this.credentials.username,
        password: this.credentials.password
      }).then(response => {
        if (response.status == 201) {
          this.requestLoading = !this.requestLoading;
        }
      });
    }
  }
};
