import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      username: "",
      password: "",
      isValid: true,
      usernameRules: {
        required: value => !!value || "Please, fill the field!",
        maxLength: value => value.length <= 20 || "Maximal length of username is 20",
        minLength: value => value.length >= 4 || "Minimal length of username is 4"
      },
      passwordRules: {
        required: value => !!value || "Please, fill the field!",
        maxLength: value => value.length <= 15 || "Maximal length of password is 15",
        minLength: value => value.length >= 4 || "Minimal length of username is 4",
        numberRequired: value => /\d/.test(value) || "Password must contain at least one number",
        whiteSpace: value => !/\s/.test(value) || "The password must be without withespace!",
        upperCase: value => /[A-Z]/.test(value) || "The password must contain at least one Upper case!",
        lowerCase: value => /[a-z]/.test(value) || "The password must contain at least one lower case!",
        nonWord: value => !/[^a-zA-Z0-9_]/.test(value) || "The password must be in Latin letters!"
      }
    };
  },
  methods: {
    register() {
      if (this.isValid == true) {
        this.requestLoading = !this.requestLoading;
        axios.post("http://192.168.1.110:8000/users/register/", {
          username: this.username,
          password: this.password
        }).then(response => {
          if (response.status == 201) {
            this.requestLoading = !this.requestLoading;
          }
        });
      }
    }
  }
};
