import { createStore } from "vuex";

export default createStore({
  state: {
    user: null,
    isAuthenticated: false,
  },
  getters: {},
  mutations: {
    SET_USER(state, user, userState) {
      state.user = user;
      state.isAuthenticated = userState;
    },
  },
  actions: {
    setUser(commit, user, userState) {
      commit("SET_USER", user, userState);
    },
  },
  modules: {},
});
