import axios from "axios";
export default {
  name: "RegisterView",
  data() {
    return {
      nemo: "",
      emailEnabled: false,
      showPassword: false,
      requestLoading: false,
      credentials: {
        username: null,
        password: null
      },
      isValid: true,
      rules: {
        required: value => !!value || "Please, fill the field!",
        maxLength: value => value.length <= 20 || "Your username must contain max 20 characters!",
        minLength: value => value.length >= 4 || "Your username must contain at least 4 characters!"
      }
    };
  },
  methods: {
    register() {
      alert(90);
      this.requestLoading = !this.requestLoading;
      axios.post("http://192.168.1.110:8000/users/register/", {
        username: this.credentials.username,
        password: this.credentials.password
      }).then(response => {
        if (response.status == 201) {
          this.requestLoading = !this.requestLoading;
        }
      });
    }
  },
  watch: {
    nemo(newValue) {
      alert(newValue);
    }
  }
};
